from aiogram import types
from aiogram.utils import executor


from aiogram.types import InlineKeyboardMarkup, InlineKeyboardButton

import random

import os

from configg import dp, bot
import logging

@dp.message_handler(commands=['start', 'help'])
async def start_handler(message:types.Message):
    await bot.send_message(message.from_user.id, f'Добро Пожаловать в Бойцовский Клуб, {message.from_user.first_name}')

@dp.message_handler(commands=['quiz'])
async def quiz_1(message: types.Message):
    markup = InlineKeyboardMarkup()
    button_call_1 = InlineKeyboardButton("next", callback_data='button_call_1')
    markup.add(button_call_1)
    question = 'чем новый айфон отличается от предыдущего?'
    answers = [
        "камерой",
        "дисплеем",
        "ничем",
        "зарядкой",
        "процессором"
    ]
    await bot.send_poll(
        chat_id=message.from_user.id,
        question=question,
        options=answers,
        is_anonymous=False,
        type='quiz',
        correct_option_id=2,
        explanation='ты серьезно?',
        reply_markup=markup


    )

@dp.callback_query_handler(lambda call: call.data == 'button_call_1')
async def quiz_2(message: types.CallbackQuery):
    markup = InlineKeyboardMarkup()
    button_call_1 = InlineKeyboardButton("next thing you do is press this button", callback_data='button_call_2')
    markup.add(button_call_1)
    question = 'Вреден ли вэйп?'
    answers = [
        "конечно",
        "да, ведь у меня рак легких 4-й стадии",
        "возможно",
        "абсолютно"
    ]
    photo = open("D:\impressive collection/wall.jpg", "rb")
    await bot.send_photo(message.from_user.id, photo=photo)
    await bot.send_poll(
        chat_id=message.from_user.id,
        question=question,
        options=answers,
        is_anonymous=False,
        type='quiz',
        correct_option_id=1,
        explanation='ты черт подери прав',
        reply_markup = markup


    )

@dp.message_handler(commands=['meme'])
async def meme_bot(message: types.Message):
  photo = open('test/' + random.choice(os.listdir('test')), 'rb')
  await bot.send_photo(message.from_user.id, photo)

@dp.message_handler()
async def echo(message: types.Message):
    if message == int:
        print(message * message)
    await bot.send_message(message.from_user.id, message.text)

if __name__ == '__main__':
    logging.basicConfig(level=logging.INFO)
    executor.start_polling(dp, skip_updates=True)

